import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Modal from 'react-bootstrap/Modal';

const BlogPreview = ({ blog, handleDelete }) => {
    return (
        <Col xs={6}>
            <Card className='blog-preview'>
                <Card.Header>{ blog.title }</Card.Header>
                <Card.Body>
                    <Card.Text>Written by {blog.author}</Card.Text>
                    <Button variant="outline-primary" onClick={() => handleDelete(blog.id)}>Delete blog</Button>
                </Card.Body>
            </Card>

            <Modal.Dialog>
                <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                </Modal.Header>

                <Modal.Body>
                    <p>¿Estás seguro de borrar { blog.title }?</p>
                </Modal.Body>

                <Modal.Footer>
                    <Button variant="secondary">Cancelar</Button>
                    <Button variant="danger">Borrar</Button>
                </Modal.Footer>
            </Modal.Dialog>
        </Col>
    );
}

export default BlogPreview;
